import React, { useState, useEffect } from 'react'
import axios from 'axios'


const FirePokemon = () => {


  let pokemon = 'charmander'
  const [data, setData] = useState(null)



  const getData = async () => {
    const response = await axios.get(`https://pokeapi.glitch.me/v1/pokemon/${pokemon}`)
    setData(response)
  }

  useEffect(() => {

    // getData()
  }, [])

    console.log('data>.', data)

  const evoOne = () => {
    pokemon = 'charmander'
    // getData()
    console.log('pokemon>', pokemon)
    console.log('data next gen>.', data)
  }

  const evoTwo = () => {
    pokemon = 'charmeleon'
    // getData()
    console.log('pokemon>', pokemon)
    console.log('data next gen>.', data)
  }
  const evoThree = () => {
    pokemon = 'char'
    // getData()
    console.log('pokemon>', pokemon)
    console.log('data next gen>.', data)
  }


  return (
      <div className='card-container'>
        <button className='pokeball' onClick={evoOne}></button>
        <button className='greatball' onClick={evoTwo}> </button>
        <button className='ultraball' onClick={evoThree}></button>
      <div className='card-holder'></div>
      <div className='pokemon-card'></div>
      <div className='fire-pokemon-img'></div>
      <div className='attributes'></div>
      <div className='stats'>
       {data?.data.map(info => {
          return (
            <>
              <p
               key={info.data}>
                {info.data}
              </p>
              <p key={info.name}>
                {info.name}
              </p>
              <p key={info.types}>
                {info.types.map(i => {
                return (
                  <p key={i}>{i}</p>
                )
              })
              }
              </p>
              <p key={info.description}>
                {info.description}
              </p>
            </>
          )
        })}
              </div>

            </div>
          )
        }



export default FirePokemon
