import axios from 'axios'


const FirePokemon = () => {

  const [data, setData] = useState(null)

  useEffect(() => {
    const getData = async () => {
      const response = await axios.get("https://pokeapi.glitch.me/v1/pokemon/4")
      setData(response)
    }
    getData()
  }, [])


  return (

